mapper.select:
SELECT a.*,b.flag as devicename 
FROM dstate a
LEFT JOIN DEVICE b ON a.deviceno=b.deviceno and a.production=b.production 


	 AND a.ID =#{id} 
	 AND a.PRODUCTION =#{production} 
	 AND a.DEVICENO =#{deviceno} 
	 AND a.STATE =#{state} 
	 AND a.DURATION =#{duration} 
	 AND unix_timestamp(a.ADDDATE) >=unix_timestamp(#{adddateFrom}) 
	 AND unix_timestamp(a.ADDDATE) <=unix_timestamp(#{adddateTo}) 
	 AND DATE_FORMAT(a.ADDDATE,'%Y-%m-%d') = #{adddate} 
	 AND a.COMMENT =#{comment} 
	 AND a.FLAG =#{flag} 
	 AND a.STATE IN ('01','02','03')  

ORDER BY a.ID DESC

SELECT a.*,b.flag as devicename 
FROM dstate a
LEFT JOIN DEVICE b ON a.deviceno=b.deviceno and a.production=b.production 

	 AND a.ID =#{id} 
	 AND a.PRODUCTION =#{production} 
	 AND a.DEVICENO =#{deviceno} 
	 AND a.STATE =#{state} 
	 AND a.DURATION =#{duration} 
	 AND unix_timestamp(a.ADDDATE) >=unix_timestamp(#{adddateFrom}) 
	 AND unix_timestamp(a.ADDDATE) <=unix_timestamp(#{adddateTo}) 
	 AND DATE_FORMAT(a.ADDDATE,'%Y-%m-%d') = #{adddate} 
	 AND a.COMMENT =#{comment} 
	 AND a.FLAG =#{flag} 
	 AND a.STATE IN ('01','02','03')  

ORDER BY a.ID DESC 
LIMIT #{fromPage},#{toPage}

SELECT COUNT(id) 
FROM dstate 

	 AND ID =#{id} 
	 AND PRODUCTION =#{production} 
	 AND DEVICENO =#{deviceno} 
	 AND STATE =#{state} 
	 AND DURATION =#{duration} 
	 AND unix_timestamp(ADDDATE) >=unix_timestamp(#{adddateFrom}) 
	 AND unix_timestamp(ADDDATE) <=unix_timestamp(#{adddateTo}) 
	 AND DATE_FORMAT(ADDDATE,'%Y-%m-%d') = #{adddate} 
	 AND COMMENT =#{comment} 
	 AND FLAG =#{flag} 
	 AND STATE IN ('01','02','03')

SELECT 
 CONCAT(b.PRODUCTION,'_',c.flag) as production ,
SUM(CASE WHEN a.STATE='01' THEN a.DURATION ELSE 0 END)  as deviceno, 
SUM(CASE WHEN a.STATE='02' THEN a.DURATION ELSE 0 END)  as state, 
SUM(CASE WHEN a.STATE='03' THEN a.DURATION ELSE 0 END)  as duration  ,
c.flag as devicename 
FROM dstate a 
LEFT JOIN prodnum b on a.production=b.id 
LEFT JOIN DEVICE c ON a.deviceno=c.deviceno and a.production=c.production 

	 AND a.ID =#{id} 
	 AND a.PRODUCTION =#{production} 
	 AND a.DEVICENO =#{deviceno} 
	 AND a.STATE =#{state} 
	 AND a.DURATION =#{duration} 
	 AND unix_timestamp(a.ADDDATE) >=unix_timestamp(#{adddateFrom}) 
	 AND unix_timestamp(a.ADDDATE) <=unix_timestamp(#{adddateTo}) 
	 AND DATE_FORMAT(a.ADDDATE,'%Y-%m-%d') = #{adddate} 
	 AND a.COMMENT =#{comment} 
	 AND a.FLAG =#{flag}  

GROUP by  CONCAT(b.PRODUCTION,'_',c.flag)

mapper.update:
UPDATE dstate
ID=#{id}, PRODUCTION=#{production}, DEVICENO=#{deviceno}, STATE=#{state}, DURATION=#{duration}, ADDDATE=#{adddate}, COMMENT=#{comment}, FLAG=#{flag},
WHERE ID=#{id}
