mapper.select:
SELECT *
FROM dstate

	 AND ID =#{id} 
	 AND PRODUCTION =#{production} 
	 AND DEVICENO =#{deviceno} 
	 AND STATE =#{state} 
	 AND unix_timestamp(ADDDATE) >=unix_timestamp(#{adddateFrom}) 
	 AND unix_timestamp(ADDDATE) <=unix_timestamp(#{adddateTo}) 
	 AND ADDDATE =#{adddate} 
	 AND COMMENT =#{comment} 
	 AND FLAG =#{flag} 
	AND STATE IN ('01','02','03') 

ORDER BY ID DESC

SELECT *
FROM dstate

	 AND ID =#{id} 
	 AND PRODUCTION =#{production} 
	 AND DEVICENO =#{deviceno} 
	 AND STATE =#{state} 
	 AND unix_timestamp(ADDDATE) >=unix_timestamp(#{adddateFrom}) 
	 AND unix_timestamp(ADDDATE) <=unix_timestamp(#{adddateTo}) 
	 AND ADDDATE =#{adddate} 
	 AND COMMENT =#{comment} 
	 AND FLAG =#{flag} 
	AND STATE IN ('01','02','03') 

ORDER BY ID DESC 
LIMIT #{fromPage},#{toPage}

SELECT COUNT(id) 
FROM dstate 

	 AND ID =#{id} 
	 AND PRODUCTION =#{production} 
	 AND DEVICENO =#{deviceno} 
	 AND STATE =#{state} 
	 AND unix_timestamp(ADDDATE) >=unix_timestamp(#{adddateFrom}) 
	 AND unix_timestamp(ADDDATE) <=unix_timestamp(#{adddateTo}) 
	 AND ADDDATE =#{adddate} 
	 AND COMMENT =#{comment} 
	 AND FLAG =#{flag} 
	AND STATE IN ('01','02','03')

mapper.update:
UPDATE dstate
ID=#{id}, PRODUCTION=#{production}, DEVICENO=#{deviceno}, STATE=#{state}, ADDDATE=#{adddate}, COMMENT=#{comment}, FLAG=#{flag},
WHERE ID=#{id}
