SELECT COUNT(id) 
FROM production 

	 AND ID =#{id} 
	 AND PRODNUM =#{prodnum} 
	 AND CHANGED =#{changed} 
	 AND YIELD =#{yield} 
	 AND STOPS =#{stops} 
	 AND PRODSTOP =#{prodstop} 
	 AND POWER =#{power} 
	 AND RATE =#{rate} 
	 AND VARIETY =#{variety} 
	 AND RHYTHM =#{rhythm} 
	 AND ITEMTIME =#{itemtime} 
	 AND REQUIRED =#{required} 
	 AND CHANGTIME =#{changtime} 
	 AND STARTCTIME =#{startctime} 
	 AND PLANCOMP =#{plancomp} 
	 AND EQUIPSTOP =#{equipstop} 
	 AND unix_timestamp(STARTTIME) >=unix_timestamp(#{starttimeFrom}) 
	 AND unix_timestamp(STARTTIME) <=unix_timestamp(#{starttimeTo}) 
	 AND STARTTIME =#{starttime} 
	 AND unix_timestamp(ENDTIME) >=unix_timestamp(#{endtimeFrom}) 
	 AND unix_timestamp(ENDTIME) <=unix_timestamp(#{endtimeTo}) 
	 AND ENDTIME =#{endtime} 
	 AND STARTSTATUS =#{startstatus} 
	 AND ACTUALCOMP =#{actualcomp} 
	 AND TOOLSTOP =#{toolstop} 
	 AND OVERTIME =#{overtime} 
	 AND PRODSTATE =#{prodstate} 
	 AND CREATER =#{creater} 
	 AND unix_timestamp(ADDDATE) >=unix_timestamp(#{adddateFrom}) 
	 AND unix_timestamp(ADDDATE) <=unix_timestamp(#{adddateTo}) 
	 AND DATE_FORMAT(ADDDATE,'%Y-%m-%d') = DATE_FORMAT(NOW(),'%Y-%m-%d') 
	 AND FLAG =#{flag}

SELECT a.*,b.production as production ,
SUM(a.actualcomp)  as allactualcomp, 
SUM(a.plancomp)  as allplancomp ,
SUM(a.actualcomp)/SUM(a.plancomp) as comrate,
group_concat(a.id) as ids ,
CASE WHEN DATE_FORMAT(a.STARTTIME,'%H:%i:%s')>='07:00:00' 
      AND DATE_FORMAT(a.STARTTIME,'%H:%i:%s')<='18:30:00' THEN '白班' ELSE '夜班' END as bc 
FROM production a 
LEFT JOIN prodnum b on a.prodnum=b.id 

	 AND a.ID =#{id} 
	 AND a.PRODNUM =#{prodnum} 
	 AND a.CHANGED =#{changed} 
	 AND a.YIELD =#{yield} 
	 AND a.STOPS =#{stops} 
	 AND a.PRODSTOP =#{prodstop} 
	 AND a.POWER =#{power} 
	 AND a.RATE =#{rate} 
	 AND a.VARIETY =#{variety} 
	 AND a.RHYTHM =#{rhythm} 
	 AND a.ITEMTIME =#{itemtime} 
	 AND a.REQUIRED =#{required} 
	 AND a.CHANGTIME =#{changtime} 
	 AND a.STARTCTIME =#{startctime} 
	 AND a.PLANCOMP =#{plancomp} 
	 AND a.EQUIPSTOP =#{equipstop} 
	 AND unix_timestamp(a.STARTTIME) >=unix_timestamp(#{starttimeFrom}) 
	 AND unix_timestamp(a.STARTTIME) <=unix_timestamp(#{starttimeTo}) 
	 AND a.STARTTIME =#{starttime} 
	 AND unix_timestamp(a.ENDTIME) >=unix_timestamp(#{endtimeFrom}) 
	 AND unix_timestamp(a.ENDTIME) <=unix_timestamp(#{endtimeTo}) 
	 AND a.ENDTIME =#{endtime} 
	 AND a.STARTSTATUS =#{startstatus} 
	 AND a.ACTUALCOMP =#{actualcomp} 
	 AND a.TOOLSTOP =#{toolstop} 
	 AND a.OVERTIME =#{overtime} 
	 AND a.PRODSTATE =#{prodstate} 
	 AND a.CREATER =#{creater} 
	 AND unix_timestamp(a.ADDDATE) >=unix_timestamp(#{adddateFrom}) 
	 AND unix_timestamp(a.ADDDATE) <=unix_timestamp(#{adddateTo}) 
	 AND DATE_FORMAT(a.ADDDATE,'%Y-%m-%d') = DATE_FORMAT(NOW(),'%Y-%m-%d') 
	 AND a.FLAG =#{flag} 
	 AND b.PRODUCTION LIKE #{production} 

GROUP BY  a.PRODNUM ,bc ,DATE_FORMAT(a.ENDTIME,'%Y-%m-%d') 
ORDER BY a.ID DESC 
LIMIT #{fromPage},#{toPage}

SELECT COUNT(*) FROM 
(
SELECT a.*,b.production as production ,
SUM(a.actualcomp)  as allactualcomp, 
SUM(a.plancomp)  as allplancomp ,
group_concat(a.id) as ids ,
CASE WHEN DATE_FORMAT(a.STARTTIME,'%H:%i:%s')>='07:00:00' 
      AND DATE_FORMAT(a.STARTTIME,'%H:%i:%s')<='18:30:00' THEN 1 ELSE 0 END as bc 
FROM production a 
LEFT JOIN prodnum b on a.prodnum=b.id 

	 AND a.ID =#{id} 
	 AND a.PRODNUM =#{prodnum} 
	 AND a.CHANGED =#{changed} 
	 AND a.YIELD =#{yield} 
	 AND a.STOPS =#{stops} 
	 AND a.PRODSTOP =#{prodstop} 
	 AND a.POWER =#{power} 
	 AND a.RATE =#{rate} 
	 AND a.VARIETY =#{variety} 
	 AND a.RHYTHM =#{rhythm} 
	 AND a.ITEMTIME =#{itemtime} 
	 AND a.REQUIRED =#{required} 
	 AND a.CHANGTIME =#{changtime} 
	 AND a.STARTCTIME =#{startctime} 
	 AND a.PLANCOMP =#{plancomp} 
	 AND a.EQUIPSTOP =#{equipstop} 
	 AND unix_timestamp(a.STARTTIME) >=unix_timestamp(#{starttimeFrom}) 
	 AND unix_timestamp(a.STARTTIME) <=unix_timestamp(#{starttimeTo}) 
	 AND a.STARTTIME =#{starttime} 
	 AND unix_timestamp(a.ENDTIME) >=unix_timestamp(#{endtimeFrom}) 
	 AND unix_timestamp(a.ENDTIME) <=unix_timestamp(#{endtimeTo}) 
	 AND a.ENDTIME =#{endtime} 
	 AND a.STARTSTATUS =#{startstatus} 
	 AND a.ACTUALCOMP =#{actualcomp} 
	 AND a.TOOLSTOP =#{toolstop} 
	 AND a.OVERTIME =#{overtime} 
	 AND a.PRODSTATE =#{prodstate} 
	 AND a.CREATER =#{creater} 
	 AND unix_timestamp(a.ADDDATE) >=unix_timestamp(#{adddateFrom}) 
	 AND unix_timestamp(a.ADDDATE) <=unix_timestamp(#{adddateTo}) 
	 AND DATE_FORMAT(a.ADDDATE,'%Y-%m-%d') = DATE_FORMAT(NOW(),'%Y-%m-%d') 
	 AND a.FLAG =#{flag} 
	 AND b.PRODUCTION LIKE #{production} 

GROUP BY  a.VARIETY ,bc ,DATE_FORMAT(a.ENDTIME,'%Y-%m-%d')
ORDER BY a.ID DESC 
) pro

