	 AND b.PRODUCTION LIKE #{production} 

GROUP BY  a.PRODNUM ,bc ,DATE_FORMAT(a.ENDTIME,'%Y-%m-%d') 
ORDER BY ${orderBy}    
LIMIT #{fromPage},#{toPage}

mapper.update:
UPDATE production
ID=#{id}, PRODNUM=#{prodnum}, CHANGED=#{changed}, YIELD=#{yield}, STOPS=#{stops}, PRODSTOP=#{prodstop}, POWER=#{power}, RATE=#{rate}, VARIETY=#{variety}, RHYTHM=#{rhythm}, ITEMTIME=#{itemtime}, REQUIRED=#{required}, CHANGTIME=#{changtime}, STARTCTIME=#{startctime}, PLANCOMP=#{plancomp}, EQUIPSTOP=#{equipstop}, STARTTIME=#{starttime}, ENDTIME=#{endtime}, STARTSTATUS=#{startstatus}, ACTUALCOMP=#{actualcomp}, TOOLSTOP=#{toolstop}, OVERTIME=#{overtime}, PRODSTATE=#{prodstate}, CREATER=#{creater}, ADDDATE=#{adddate}, FLAG=#{flag},
WHERE ID=#{id}
