mapper.select:
SELECT *
FROM production

	 AND ID =#{id} 
	 AND PRODUCTION =#{production} 
	 AND CHANGED =#{changed} 
	 AND YIELD =#{yield} 
	 AND PRODSTOP =#{prodstop} 
	 AND POWER =#{power} 
	 AND RATE =#{rate} 
	 AND VARIETY =#{variety} 
	 AND RHYTHM =#{rhythm} 
	 AND PLANCOMP =#{plancomp} 
	 AND EQUIPSTOP =#{equipstop} 
	 AND unix_timestamp(STARTTIME) >=unix_timestamp(#{starttimeFrom}) 
	 AND unix_timestamp(STARTTIME) <=unix_timestamp(#{starttimeTo}) 
	 AND STARTTIME =#{starttime} 
	 AND ACTUALCOMP =#{actualcomp} 
	 AND TOOLSTOP =#{toolstop} 
	 AND OVERTIME =#{overtime} 
	 AND PRODSTATE =#{prodstate} 
	 AND CREATER =#{creater} 
	 AND unix_timestamp(ADDDATE) >=unix_timestamp(#{adddateFrom}) 
	 AND unix_timestamp(ADDDATE) <=unix_timestamp(#{adddateTo}) 
	 AND DATE_FORMAT(ADDDATE,'%Y-%m-%d') = DATE_FORMAT(NOW(),'%Y-%m-%d') 
	 AND FLAG =#{flag} 

ORDER BY ID DESC 
LIMIT #{fromPage},#{toPage}

SELECT COUNT(id) 
FROM production 

	 AND ID =#{id} 
	 AND PRODUCTION =#{production} 
	 AND CHANGED =#{changed} 
	 AND YIELD =#{yield} 
	 AND PRODSTOP =#{prodstop} 
	 AND POWER =#{power} 
	 AND RATE =#{rate} 
	 AND VARIETY =#{variety} 
	 AND RHYTHM =#{rhythm} 
	 AND PLANCOMP =#{plancomp} 
	 AND EQUIPSTOP =#{equipstop} 
	 AND unix_timestamp(STARTTIME) >=unix_timestamp(#{starttimeFrom}) 
	 AND unix_timestamp(STARTTIME) <=unix_timestamp(#{starttimeTo}) 
	 AND STARTTIME =#{starttime} 
	 AND ACTUALCOMP =#{actualcomp} 
	 AND TOOLSTOP =#{toolstop} 
	 AND OVERTIME =#{overtime} 
	 AND PRODSTATE =#{prodstate} 
	 AND CREATER =#{creater} 
	 AND unix_timestamp(ADDDATE) >=unix_timestamp(#{adddateFrom}) 
	 AND unix_timestamp(ADDDATE) <=unix_timestamp(#{adddateTo}) 
	 AND ADDDATE =#{adddate} 
	 AND FLAG =#{flag}

mapper.update:
UPDATE production
ID=#{id}, PRODUCTION=#{production}, CHANGED=#{changed}, YIELD=#{yield}, PRODSTOP=#{prodstop}, POWER=#{power}, RATE=#{rate}, VARIETY=#{variety}, RHYTHM=#{rhythm}, PLANCOMP=#{plancomp}, EQUIPSTOP=#{equipstop}, STARTTIME=#{starttime}, ACTUALCOMP=#{actualcomp}, TOOLSTOP=#{toolstop}, OVERTIME=#{overtime}, PRODSTATE=#{prodstate}, CREATER=#{creater}, ADDDATE=#{adddate}, FLAG=#{flag},
WHERE ID=#{id}
